alias saferm='function safermf(){ f=$1 && printf "\nsaferm: stop if no write permission to $f ...\n" && [[ -w "$f" ]] && t=$(date -Ins) && printf "\nsaferm: stop if no write permission to parent dir of $f ...\n" && mv -v "$f" "$f"-saferm-"$t" &&  mv -v "$f"-saferm-"$t" "$f" && printf "\nsaferm: stop if cannot delete all files below $f -- files already deleted are inside /fred/oz120/saferm-$t.tar ...\n" && tar --remove-files -cf '$1'/saferm-"$t".tar "$f" && rm -fv '$1'/saferm-"$t".tar && printf "\nsaferm: successfully removed $f\n"; }; safermf'
