alias safermrf='function safermrff(){ f=$1 && printf "\nsafermrf: stop if no write permission to $f ...\n" && [[ -w "$f" ]] && t=$(date -Ins) && printf "\nsafermrf: stop if no write permission to parent dir of $f ...\n" && mv -v "$f" PARTIAL-"$f"-safermrf-"$t" &&  printf "\nsafermrf: stop if cannot delete all files below PARTIAL-$f-safermrf-$t -- files already deleted are inside '"$1"'/safermrf-$t.tar ...\n" && tar --remove-files -cf '"$1"'/safermrf-"$t".tar PARTIAL-"$f"-safermrf-"$t" && rm -fv '"$1"'/safermrf-"$t".tar && printf "\nsafermrf: successfully removed $f\n"; }; safermrff'
